import { describe } from './expect.js'
import { Http } from './http.js'

const http = new Http()

export default function () {
  describe('document key checks', (t) => {
    const schema = {
      '@id': 'Child',
      '@type': 'Class',
      '@subdocument': [],
    }
    {
      const r = http.post(`/api/db/${http.user}/dbname`, '{"comment":"c","label":"l"}', { headers: { 'Content-Type': 'application/json' } })
      t.expect(r.status).as('status').toEqual(200)
        .and(r).toHaveValidJson()
        .and(r.json('@type')).as('@type').toEqual('api:DbCreateResponse')
        .and(r.json('api:status')).as('api:status').toEqual('api:success')
    }
    {
      const r = http.post(`/api/document/${http.user}/dbname?graph_type=schema&author=a&message=m`, JSON.stringify(schema), { headers: { 'Content-Type': 'application/json' } })
      t.expect(r.status).as('status').toEqual(400)
      t.expect(r).toHaveValidJson()
        .and(r.json('api:status')).as('api:status').toEqual('api:failure')
        .and(r.json('api:error.@type')).as('api:error.@type').toEqual('api:SubdocumentKeyMissing')
        .and(r.json('api:error.api:document.@id')).as('api:error.api:document.@id').toEqual('Child')
    }
    {
      schema['@key'] = { useless_key: 'useless_value' }
      const r = http.post(`/api/document/${http.user}/dbname?graph_type=schema&author=a&message=m`, JSON.stringify(schema), { headers: { 'Content-Type': 'application/json' } })
      t.expect(r.status).as('status').toEqual(400)
      t.expect(r).toHaveValidJson()
        .and(r.json('api:status')).as('api:status').toEqual('api:failure')
        .and(r.json('api:error.@type')).as('api:error.@type').toEqual('api:DocumentKeyTypeMissing')
        .and(r.json('api:error.api:document.@id')).as('api:error.api:document.@id').toEqual('Child')
        .and(r.json('api:error.api:key')).as('api:error.api:key').toEqual(JSON.stringify(schema['@key']))
    }
    {
      schema['@key'] = { '@type': 'Unknown' }
      const r = http.post(`/api/document/${http.user}/dbname?graph_type=schema&author=a&message=m`, JSON.stringify(schema), { headers: { 'Content-Type': 'application/json' } })
      t.expect(r.status).as('status').toEqual(400)
      t.expect(r).toHaveValidJson()
        .and(r.json('api:status')).as('api:status').toEqual('api:failure')
        .and(r.json('api:error.@type')).as('api:error.@type').toEqual('api:DocumentKeyTypeUnknown')
        .and(r.json('api:error.api:document.@id')).as('api:error.api:document.@id').toEqual('Child')
        .and(r.json('api:error.api:key_type')).as('api:error.api:key_type').toEqual('Unknown')
    }
    {
      const r = http.delete(`/api/db/${http.user}/dbname`)
      t.expect(r.status).as('status').toEqual(200)
        .and(r).toHaveValidJson()
        .and(r.json('@type')).as('@type').toEqual('api:DbDeleteResponse')
        .and(r.json('api:status')).as('api:status').toEqual('api:success')
    }
  })
}
